    private void btnhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnhapusActionPerformed
        // TODO add your handling code here:
        try {
 String sql ="delete from data_obat where id_obat ='"+formid.getText()+"'";
 java.sql.Connection 
conn=koneksi.configDB();
 java.sql.PreparedStatement 
pst=conn.prepareStatement(sql);
 pst.execute();
 JOptionPane.showMessageDialog(this, "berhasil di hapus");
 } catch (Exception e) {
 JOptionPane.showMessageDialog(this, 
e.getMessage());
 }
 load_tabel();
 clearForm();
        
    }//GEN-LAST:event_btnhapusActionPerformed

    private void btntambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btntambahActionPerformed
        // TODO add your handling code here:
        try {
 String sql = "INSERT INTO data_obat VALUES ('"+formnama.getText()+"','"+formid.getText()+"','"+formjenis.getText()+"','"+formrasa.getText()+"','"+formharga.getText()+"','"+formexp.getText()+"')";
 java.sql.Connection
conn=koneksi.configDB();
 java.sql.PreparedStatement
pst=conn.prepareStatement(sql);
 pst.execute();
 JOptionPane.showMessageDialog(null,"Penyimpanan Data Berhasil");
load_tabel();
clearForm();
 }
catch (Exception e) {
 JOptionPane.showMessageDialog(this,
e.getMessage());
 }
        
    }//GEN-LAST:event_btntambahActionPerformed

    private void btneditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btneditActionPerformed
        // TODO add your handling code here:
        try {
 String sql ="UPDATE data_obat SET namaobat = '"+formnama.getText()+"', id_obat = '"+formid.getText()+"', jenisobat = '"+formjenis.getText()+"', rasaobat = '"+formrasa.getText()+"', hargaobat = '"+formharga.getText()+"', expobat = '"+formexp.getText()+"' WHERE id_obat = '"+formid.getText()+"'";
 java.sql.Connection 
conn=koneksi.configDB();
 java.sql.PreparedStatement 
pst=conn.prepareStatement(sql);
 pst.execute();
 JOptionPane.showMessageDialog(null, "data berhasil di edit");
 } catch (Exception e) {
 JOptionPane.showMessageDialog(null, "Perubahan Data Gagal"+e.getMessage());
 }
 load_tabel();
 clearForm();

    }//GEN-LAST:event_btneditActionPerformed

    private void tabeldataMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabeldataMouseClicked
        // TODO add your handling code here:
        int baris = tabeldata.rowAtPoint(evt.getPoint());
 String namaobat = tabeldata.getValueAt(baris,1).toString();
 formnama.setText(namaobat);
 String id_obat =tabeldata.getValueAt(baris,2).toString();
 formid.setText(id_obat);
 String jenisobat = tabeldata.getValueAt(baris,3).toString();
 formjenis.setText(jenisobat);
 String rasaobat = tabeldata.getValueAt(baris,4).toString();
 formrasa.setText(rasaobat);
 String hargaobat = tabeldata.getValueAt(baris,5).toString();
 formharga.setText(hargaobat);
 String expobat = tabeldata.getValueAt(baris,6).toString();
 formexp.setText(expobat);
    }//GEN-LAST:event_tabeldataMouseClicked

