    private void load_tabel() {
 {
    DefaultTableModel model = new 
    DefaultTableModel();
    model.addColumn("NO");
    model.addColumn("NAMA");
    model.addColumn("ID OBAT");
    model.addColumn("JENIS OBAT");
    model.addColumn("RASA OBAT");
    model.addColumn("HARGA OBAT");
    model.addColumn("EXP OBAT");
 //menampilkan data database kedalam tabel
 try {
 int no=1;
 String sql = "select * from data_obat";
 java.sql.Connection 
conn=koneksi.configDB();
 java.sql.Statement 
stm=conn.createStatement();
 java.sql.ResultSet 
res=stm.executeQuery(sql);
 while(res.next()){
 model.addRow(new 
Object[]{no++,res.getString(1),res.getString(2),res.getString(3),res.getString(4),res.getString(5),res.getString(6)});
 }
 
    tabeldata.setModel(model);
 
       } 
      catch (SQLException e) {
      }
 }
}
